mapper.select:
select tt.rm, tt.yyyymm, tt.cnt, tt.accountflag, tt.Gubun from ( select rownum as rm, m.yyyymm, m.cnt, m.accountflag, m.Gubun from( select to_date(substr(to_char(in_date,'yyyymm'),0,7),'yyyymm') yyyymm, count(*) cnt, (case when class_account_flag = 'Y' then '정산' when class_account_flag = 'N' then '미정산' end) accountflag, '온라인클래스' Gubun
from buy_lesson a group by to_date(substr(to_char(in_date,'yyyymm'),0,7),'yyyymm'),class_account_flag,'온라인클래스'
union all
select to_date(substr(to_char(in_date,'yyyymm'),0,7),'yyyymm') yyyymm, count(*) cnt, (case when chatting_account_flag = 'Y' then '정산' when chatting_account_flag = 'N' then '미정산' end) accountflag, '채팅' as Gubun
from chatting b group by to_date(substr(to_char(in_date,'yyyymm'),0,7),'yyyymm') ,chatting_account_flag,'채팅'
)m where 1=1
and m.accountflag = '정산'
and m.accountflag = '미정산'
and to_char(m.yyyymm, 'YYYYMM') = #{selDate}
and m.Gubun = '온라인클래스'
and m.Gubun = '채팅'
order by m.yyyymm desc, m.gubun, m.accountflag
)tt where rm between #{rowStart} and #{rowEnd}

select tt.rm, tt.yyyymm, tt.trainer_id, tt.cnt, tt.sum, tt.fee, tt.realamount, tt.Gubun from ( select rownum as rm, m.yyyymm, m.trainer_id, m.cnt, m.sum, m.fee, m.realamount, m.Gubun from( select substr(to_char(a.in_date,'yyyy-mm'),0,7) yyyymm, b.in_user_id trainer_id, count(*) as cnt, sum(lesson_price) as sum, round(sum(lesson_price) * 0.2,-1) as fee, sum(lesson_price) - round(sum(lesson_price) * 0.2,-1) realamount, '온라인클래스' Gubun
from buy_lesson a left join lesson b on (a.lesson_seq = b.lesson_seq) left join member c on (b.in_user_id = c.member_id) group by substr(to_char(a.in_date,'yyyy-mm'),0,7) , b.in_user_id, '온라인클래스'
union all
select substr(to_char(a.in_date,'yyyy-mm'),0,7) yyyymm, b.chat_profile_id trainer_id, count(*) as cnt, count(*) * 3200 as sum, round(count(*) * 3200 * 0.2,-1) as fee, count(*) * 3200 - round(count(*) * 3200 * 0.2,-1) realamount, '채팅' Gubun
from chatting a left join chat_profile b on (a.chat_profile_seq = b.chat_profile_seq) group by substr(to_char(a.in_date,'yyyy-mm'),0,7) , b.chat_profile_id, '채팅'
)m where m.yyyymm = #{yyyymm} and m.gubun = #{gubun}
order by m.yyyymm desc, m.gubun
)tt where rm between #{rowStart} and #{rowEnd}
